/**
 * Script de depuración para HTMX Infinite Scroll
 */

document.addEventListener('DOMContentLoaded', function() {
    console.log(' HTMX Debug Script cargado');

    // Verificar si HTMX está cargado
    if (typeof htmx !== 'undefined') {
        console.log('✅ HTMX está cargado correctamente');
        console.log('   Versión:', htmx.version || 'No disponible');
    } else {
        console.error('❌ HTMX NO está cargado');
        return;
    }

    // Escuchar eventos de HTMX
    document.body.addEventListener('htmx:beforeRequest', function(evt) {
        console.log(' HTMX: Enviando petición', evt.detail);
    });

    document.body.addEventListener('htmx:afterRequest', function(evt) {
        console.log(' HTMX: Petición completada', {
            status: evt.detail.xhr.status,
            url: evt.detail.xhr.responseURL
        });
    });

    document.body.addEventListener('htmx:afterSettle', function(evt) {
        console.log('✅ HTMX: Contenido insertado', evt.detail);
    });

    document.body.addEventListener('htmx:responseError', function(evt) {
        console.error('❌ HTMX: Error en respuesta', evt.detail);
    });

    // Verificar el trigger
    const trigger = document.querySelector('.htmx-scroll-trigger');
    if (trigger) {
        console.log('✅ Trigger encontrado:', trigger);
        console.log('   Atributos HTMX:', {
            'hx-get': trigger.getAttribute('hx-get'),
            'hx-trigger': trigger.getAttribute('hx-trigger'),
            'hx-swap': trigger.getAttribute('hx-swap')
        });

        // Observar cuando el trigger sea visible
        const observer = new IntersectionObserver((entries) => {
            entries.forEach(entry => {
                if (entry.isIntersecting) {
                    console.log('️ Trigger es VISIBLE en el viewport');
                } else {
                    console.log(' Trigger NO es visible');
                }
            });
        }, { threshold: 0.5 });

        observer.observe(trigger);
    } else {
        console.warn('⚠️ No se encontró el trigger de HTMX');
    }

    // Verificar el contenedor .feed
    const feed = document.querySelector('.feed');
    if (feed) {
        console.log('✅ Contenedor .feed encontrado');
        console.log('   scrollHeight:', feed.scrollHeight);
        console.log('   clientHeight:', feed.clientHeight);
        console.log('   overflow-y:', window.getComputedStyle(feed).overflowY);
    }
});
